mapper.select:
SELECT password
FROM member
WHERE userId = #{userId} AND email = #{email}

mapper.update:
UPDATE member
SET
password = #{password}
WHERE
name = #{name}
AND userId = #{userId}
AND
email = #{email}

mapper.delete:
delete from member
where userId = #{userId} and
password = #{password}

DELETE FROM member
WHERE userId = #{userId}
AND
password = #{password}
