mapper.select:
		 select teacher_id, bootcamp_id, teacher_name, bootcamp_name, course_name, description, image_url
         from teacher
        where teacher_id = #{teacher_id}

mapper.update:
UPDATE teacher 
   SET TEACHER_NAME = #{teacher_name}, COURSE_NAME = #{course_name}, SHORT_DESCRIPTION = #{short_description}, DESCRIPTION = #{description}, IMAGE_URL = #{image_url} 
 WHERE TEACHER_ID = #{teacher_id}
