mapper.insert:
  		INSERT INTO COURSE (COURSE_ID, COURSE_NAME, DESCRIPTION, PRICE, BOOTCAMP_ID, BOOTCAMP_NAME, USERID, COURSE_PERIOD, POSITION_ID
  			
  				,FNAME, FNAME_EN, FSIZE
  			
  			) 
  		VALUES (seq_course_course_id.NEXTVAL, #{course_name}, #{description}, #{price}, #{bootcamp_id}, #{bootcamp_name}, #{userId}, #{course_period}, #{position_id}
  			
		 	, #{fname}, #{fname_en}, #{fsize}
		 	
		 	)

mapper.update:
UPDATE COURSE SET COURSE_NAME = #{course_name}, DESCRIPTION = #{description}, PRICE = #{price}, BOOTCAMP_ID = #{bootcamp_id}, BOOTCAMP_NAME = #{bootcamp_name}, USERID = #{userId}, COURSE_PERIOD = #{course_period}, POSITION_ID = #{position_id}
, FNAME = #{fname}, FNAME_EN = #{fname_en}, FSIZE = #{fsize}
WHERE COURSE_ID = #{course_id}
