mapper.select:
SELECT password
FROM member
WHERE userid = #{userid} AND email = #{email}

mapper.update:
UPDATE member
SET
password = #{password}
WHERE
name = #{name}
AND userid = #{userid}
AND
email = #{email}

mapper.delete:
delete from member
where userid = #{userid} and
password = #{password}

DELETE FROM member
WHERE userid = #{userid}
AND
password = #{password}
